import Taro from "@tarojs/taro";
import { Image, View } from "@tarojs/components";
import { AtActivityIndicator } from "taro-ui";

interface Props {
  customStyle?: string;
  customImageStyle?: string;
  status: "loading" | "noLoading";
  imageUrl?: string;
}

const SloganImage =
  "data:image/png;base64,";
export default function LoadMore({
  customStyle,
  customImageStyle,
  status,
  imageUrl = SloganImage
}: Props) {
  const sloganComponent = (
    <Image
      src={imageUrl}
      mode="widthFix"
      className="bottom-slogan fade-in"
      style={customImageStyle}
    />
  );
  return (
    <View className="load-more-wrapper" style={customStyle}>
      {status === "loading" && (
        <View>
          <AtActivityIndicator isOpened />
        </View>
      )}
      {status === "noLoading" && <View>{sloganComponent}</View>}
    </View>
  );
}
LoadMore.defaultProps = {
  status: "noLoading"
};
LoadMore.options = {
  addGlobalClass: true
};
